import { HttpErrorResponse } from '@angular/common/http';
import * as i0 from "@angular/core";
import * as i1 from "@angular/common/http";
export class DonutShopService {
    constructor(httpClient, newBaseUrl) {
        this.httpClient = httpClient;
        this.aspBaseUrl = "";
        this.donutApiUrl = "https://grandcircusco.github.io/demo-apis/donuts.json";
        this.donutDetailUrl = "https://grandcircusco.github.io/demo-apis/donuts/";
        this.donutCart = new Cart();
        this.aspBaseUrl = newBaseUrl;
    }
    async getDonutList() {
        let callResult = null;
        try {
            callResult = await this.httpClient.get(this.donutApiUrl).toPromise();
            console.log('Service received ' + callResult.count.toString() + ' users');
        }
        catch (unexpectedException) {
            callResult = [];
            callResult.data = [];
            if (unexpectedException instanceof HttpErrorResponse) {
                let unexpectedExceptionHttp = unexpectedException;
                console.log('Error sending http request ' + this.donutApiUrl + ': ' + unexpectedExceptionHttp.message);
            }
        }
        return callResult.results;
    }
    async getDonutDetails(id) {
        let thisDonut = null;
        try {
            let donutString = this.donutDetailUrl;
            donutString = donutString + id + ".json";
            thisDonut = await this.httpClient.get(donutString).toPromise();
            console.log('Donut details for Donut' + id + 'were successfully retrieved');
        }
        catch (unexpectedException) {
            thisDonut = [];
            if (unexpectedException instanceof HttpErrorResponse) {
                let unexpectedExceptionHttp = unexpectedException;
                console.log('Cannot get donut with ID ' + id + ':' + unexpectedExceptionHttp.message);
            }
        }
        return thisDonut;
    }
    async addToCart(id) {
        let donutToCart = await this.getDonutDetails(id);
        this.donutCart.items.push(donutToCart);
    }
    showCart() {
        return this.donutCart.items;
    }
}
